import React, { useState, useEffect } from "react";
import axios from "axios";
import "./CustomerDashboard.css";

const CustomerDashboard = () => {
  const [activeTab, setActiveTab] = useState("submit");
  const [userData, setUserData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    nic: "",
  });

  const [complaintData, setComplaintData] = useState({
    name: "",
    nationalId: "",
    contact: "",
    email: "",
    address: "",
    description: "",
    department: "Select Department",
    urgency: "High",
    submittedAt: new Date().toISOString().slice(0, 16),
  });

  const [complaints, setComplaints] = useState([]);
  const [sortType, setSortType] = useState("date-asc");

  // Load citizen data from backend using email from localStorage
  useEffect(() => {
    const email = localStorage.getItem("citizenEmail");
    if (email) {
      axios
        .get(`http://localhost:8081/Complaint-chain/api/auth/citizen?email=${email}`)
        .then((res) => {
          setUserData(res.data);
          // Auto-fill form fields
          setComplaintData((prev) => ({
            ...prev,
            name: res.data.firstName + " " + res.data.lastName,
            nationalId: res.data.nic,
            email: res.data.email,
            contact: res.data.phone,
          }));
        })
        .catch((err) => {
          console.error("Error fetching citizen data:", err);
        });
    } else {
      console.warn("No email found in localStorage.");
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setComplaintData({ ...complaintData, [name]: value });
  };

  const handleSubmitComplaint = (e) => {
    e.preventDefault();
    const newComplaint = {
      id: complaints.length + 1,
      ...complaintData,
      status: "Pending",
    };
    setComplaints([...complaints, newComplaint]);
    alert(`Complaint submitted:\n${JSON.stringify(newComplaint, null, 2)}`);

    setComplaintData({
      ...complaintData,
      description: "",
      department: "Select Department",
      urgency: "High",
      submittedAt: new Date().toISOString().slice(0, 16),
    });
  };

  const handleSortChange = (value) => setSortType(value);

  const sortedComplaints = [...complaints].sort((a, b) => {
    switch (sortType) {
      case "date-asc":
        return new Date(a.submittedAt) - new Date(b.submittedAt);
      case "date-desc":
        return new Date(b.submittedAt) - new Date(a.submittedAt);
      case "priority-asc":
        return a.urgency.localeCompare(b.urgency);
      case "priority-desc":
        return b.urgency.localeCompare(a.urgency);
      case "overall-asc":
        return new Date(a.submittedAt) - new Date(b.submittedAt) || a.urgency.localeCompare(b.urgency);
      case "overall-desc":
        return new Date(b.submittedAt) - new Date(a.submittedAt) || b.urgency.localeCompare(a.urgency);
      default:
        return 0;
    }
  });

  return (
    <div className="cust-home-page">
      <div className="cust-home-container">
        <h1>Customer Dashboard</h1>

        {/* Tabs */}
        <div className="cust-btn-group">
          <button
            className={`cust-btn ${activeTab === "submit" ? "cust-btn-active" : ""}`}
            onClick={() => setActiveTab("submit")}
          >
            Submit Complaint
          </button>
          <button
            className={`cust-btn ${activeTab === "track" ? "cust-btn-active" : ""}`}
            onClick={() => setActiveTab("track")}
          >
            Track Complaint
          </button>
          <button
            className={`cust-btn ${activeTab === "profile" ? "cust-btn-active" : ""}`}
            onClick={() => setActiveTab("profile")}
          >
            User Profile
          </button>
        </div>

        {/* Tab Content */}
        <div className="cust-tab-content">
          {/* Submit Complaint */}
          {activeTab === "submit" && (
            <div className="cust-tab-box">
              <h3>Submit Complaint</h3>
              <form onSubmit={handleSubmitComplaint} className="cust-form-grid">
                <div className="form-group">
                  <label>Name of Citizen:</label>
                  <input type="text" name="name" value={complaintData.name} onChange={handleChange} readOnly />
                </div>
                <div className="form-group">
                  <label>National ID Number:</label>
                  <input type="text" name="nationalId" value={complaintData.nationalId} onChange={handleChange} readOnly />
                </div>
                <div className="form-group">
                  <label>Contact Number:</label>
                  <input type="text" name="contact" value={complaintData.contact} onChange={handleChange} readOnly />
                </div>
                <div className="form-group">
                  <label>Email Address:</label>
                  <input type="email" name="email" value={complaintData.email} onChange={handleChange} readOnly />
                </div>
                <div className="form-group">
                  <label>Address:</label>
                  <input type="text" name="address" placeholder="Enter Address" value={complaintData.address} onChange={handleChange} required />
                </div>
                <div className="form-group full-width">
                  <label>Description:</label>
                  <textarea name="description" placeholder="Enter Complaint Description" value={complaintData.description} onChange={handleChange} required rows={5} />
                </div>
                <div className="form-group">
                  <label>Department:</label>
                  <select name="department" value={complaintData.department} onChange={handleChange} required>
                    <option disabled>Select Department</option>
                    <option>Health</option>
                    <option>Education</option>
                    <option>Transport</option>
                    <option>Utilities</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Priority:</label>
                  <select name="urgency" value={complaintData.urgency} onChange={handleChange} required>
                    <option>High</option>
                    <option>Low</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Submission Date & Time:</label>
                  <input type="datetime-local" name="submittedAt" value={complaintData.submittedAt} onChange={handleChange} required />
                </div>
                <div className="form-group full-width" style={{ textAlign: "center" }}>
                  <button type="submit" className="cust-btn-submit">Submit</button>
                </div>
              </form>
            </div>
          )}

          {/* Track Complaint */}
          {activeTab === "track" && (
            <div className="cust-tab-box">
              <h3>Track Complaint</h3>
              <div style={{ display: "flex", gap: "10px", marginBottom: "12px", flexWrap: "wrap", alignItems: "center" }}>
                <label style={{ color: "#ddd" }}>Sort by:</label>
                <select onChange={(e) => handleSortChange(e.target.value)}>
                  <option value="date-asc">Date Ascending</option>
                  <option value="date-desc">Date Descending</option>
                  <option value="priority-asc">Priority Low → High</option>
                  <option value="priority-desc">Priority High → Low</option>
                  <option value="overall-asc">Overall Ascending</option>
                  <option value="overall-desc">Overall Descending</option>
                </select>
              </div>
              <table className="cust-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Department</th>
                    <th>Priority</th>
                    <th>Description</th>
                    <th>Submission Date & Time</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {sortedComplaints.map((c) => (
                    <tr key={c.id}>
                      <td>{c.id}</td>
                      <td>{c.name}</td>
                      <td>{c.department}</td>
                      <td>{c.urgency}</td>
                      <td>{c.description}</td>
                      <td>{c.submittedAt}</td>
                      <td>{c.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* User Profile */}
          {activeTab === "profile" && (
            <div className="cust-tab-box">
              <h3>User Profile</h3>
              <div className="cust-profile">
                <p><strong>Name:</strong> {userData.firstName} {userData.lastName}</p>
                <p><strong>Email:</strong> {userData.email}</p>
                <p><strong>Phone:</strong> {userData.phone}</p>
                <p><strong>NIC:</strong> {userData.nic}</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
