import React, { useState, useEffect } from "react";
import axios from "axios";
import "./CustomerDashboard.css";

const CustomerDashboard = () => {
  const [activeTab, setActiveTab] = useState("submit");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const [userData, setUserData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    nic: "",
    phone: "",
  });

  const [complaintData, setComplaintData] = useState({
    name: "",
    nationalId: "",
    contact: "",
    email: "",
    address: "",
    description: "",
    department: "Select Department",
    urgency: "High",
    submittedAt: new Date().toISOString().slice(0, 16),
  });

  const [complaints, setComplaints] = useState([]);
  const [sortType, setSortType] = useState("date-asc");

  // Fetch citizen data and complaints on mount
  useEffect(() => {
    const email = localStorage.getItem("citizenEmail");
    if (!email) {
      setError("No email found. Please log in first.");
      setLoading(false);
      return;
    }

    const fetchCitizen = axios.get(
      `http://localhost:8081/Complaint-chain/api/auth/citizen?email=${email}`
    );
    const fetchComplaints = axios.get(
      `http://localhost:8081/Complaint-chain/api/complaints?email=${email}`
    );

    Promise.all([fetchCitizen, fetchComplaints])
      .then(([citizenRes, complaintsRes]) => {
        const data = citizenRes.data;

        setUserData(data);
        setComplaintData((prev) => ({
          ...prev,
          name: `${data.firstName} ${data.lastName}`,
          nationalId: data.nic,
          contact: data.phone,
          email: data.email,
        }));

        setComplaints(complaintsRes.data);
      })
      .catch((err) => setError("Error fetching data."))
      .finally(() => setLoading(false));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setComplaintData({ ...complaintData, [name]: value });
  };

  const handleSubmitComplaint = (e) => {
    e.preventDefault();

    const newComplaint = {
      ...complaintData,
      status: "Pending",
    };

    axios
      .post("http://localhost:8081/Complaint-chain/api/complaints", newComplaint)
      .then((res) => {
        setComplaints([...complaints, res.data]);
        alert("Complaint submitted successfully!");
        setComplaintData((prev) => ({
          ...prev,
          description: "",
          address: "",
          department: "Select Department",
          urgency: "High",
          submittedAt: new Date().toISOString().slice(0, 16),
        }));
      })
      .catch(() => alert("Error submitting complaint"));
  };

  const handleSortChange = (value) => setSortType(value);

  const sortedComplaints = [...complaints].sort((a, b) => {
    switch (sortType) {
      case "date-asc":
        return new Date(a.submittedAt) - new Date(b.submittedAt);
      case "date-desc":
        return new Date(b.submittedAt) - new Date(a.submittedAt);
      case "priority-asc":
        return a.urgency.localeCompare(b.urgency);
      case "priority-desc":
        return b.urgency.localeCompare(a.urgency);
      default:
        return 0;
    }
  });

  if (loading) return <p>Loading dashboard...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="cust-home-page">
      <div className="cust-home-container">
        <h1>Customer Dashboard</h1>

        {/* Tabs */}
        <div className="cust-btn-group">
          {["submit", "track", "profile"].map((tab) => (
            <button
              key={tab}
              className={`cust-btn ${activeTab === tab ? "cust-btn-active" : ""}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab === "submit"
                ? "Submit Complaint"
                : tab === "track"
                ? "Track Complaint"
                : "User Profile"}
            </button>
          ))}
        </div>

        <div className="cust-tab-content">
          {/* Submit Complaint */}
          {activeTab === "submit" && (
            <div className="cust-tab-box">
              <h3>Submit Complaint</h3>
              <form onSubmit={handleSubmitComplaint} className="cust-form-grid">
                <input type="hidden" name="email" value={complaintData.email} />
                <div className="form-group">
                  <label>Name of Citizen:</label>
                  <input type="text" name="name" value={complaintData.name} readOnly />
                </div>
                <div className="form-group">
                  <label>National ID Number:</label>
                  <input type="text" name="nationalId" value={complaintData.nationalId} readOnly />
                </div>
                <div className="form-group">
                  <label>Contact Number:</label>
                  <input type="text" name="contact" value={complaintData.contact} readOnly />
                </div>
                <div className="form-group">
                  <label>Email Address:</label>
                  <input type="email" name="email" value={complaintData.email} readOnly />
                </div>
                <div className="form-group">
                  <label>Address:</label>
                  <input type="text" name="address" value={complaintData.address} onChange={handleChange} required />
                </div>
                <div className="form-group full-width">
                  <label>Description:</label>
                  <textarea name="description" value={complaintData.description} onChange={handleChange} rows={5} required />
                </div>
                <div className="form-group">
                  <label>Department:</label>
                  <select name="department" value={complaintData.department} onChange={handleChange} required>
                    <option disabled>Select Department</option>
                    <option>Health</option>
                    <option>Education</option>
                    <option>Transport</option>
                    <option>Utilities</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Priority:</label>
                  <select name="urgency" value={complaintData.urgency} onChange={handleChange}>
                    <option>High</option>
                    <option>Low</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>Submission Date & Time:</label>
                  <input type="datetime-local" name="submittedAt" value={complaintData.submittedAt} onChange={handleChange} />
                </div>
                <div className="form-group full-width" style={{ textAlign: "center" }}>
                  <button type="submit" className="cust-btn-submit">Submit</button>
                </div>
              </form>
            </div>
          )}

          {/* Track Complaint */}
          {activeTab === "track" && (
            <div className="cust-tab-box">
              <h3>Track Complaint</h3>
              <div style={{ marginBottom: "12px" }}>
                <label style={{ color: "#ddd" }}>Sort by:</label>
                <select onChange={(e) => handleSortChange(e.target.value)}>
                  <option value="date-asc">Date Ascending</option>
                  <option value="date-desc">Date Descending</option>
                  <option value="priority-asc">Priority Low → High</option>
                  <option value="priority-desc">Priority High → Low</option>
                </select>
              </div>
              <table className="cust-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Department</th>
                    <th>Priority</th>
                    <th>Description</th>
                    <th>Submission Date & Time</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {sortedComplaints.map((c) => (
                    <tr key={c.id}>
                      <td>{c.id}</td>
                      <td>{c.department}</td>
                      <td>{c.urgency}</td>
                      <td>{c.description}</td>
                      <td>{c.submittedAt}</td>
                      <td>{c.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* User Profile */}
          {activeTab === "profile" && (
            <div className="cust-tab-box">
              <h3>User Profile</h3>
              <p><strong>Name:</strong> {userData.firstName} {userData.lastName}</p>
              <p><strong>Email:</strong> {userData.email}</p>
              <p><strong>Phone:</strong> {userData.phone}</p>
              <p><strong>NIC:</strong> {userData.nic}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
