import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "./CustomerDashboard.module.css";

const CustomerDashboard = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    contact: "",
    address: "",
    department: "",
    description: "",
    national_id: "",
    urgency: "",
  });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [complaints, setComplaints] = useState([]);

  // Fetch all complaints on load
  useEffect(() => {
    fetchComplaints();
  }, []);

  const fetchComplaints = async () => {
    try {
      const response = await axios.get(
        "http://localhost:8081/Complaint-chain/api/complaints/all"
      );
      setComplaints(response.data);
    } catch (err) {
      console.log("Error fetching complaints:", err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
    setErrors({ ...errors, [e.target.id]: "" }); // clear error while typing
  };

  const handleSubmitComplaint = async (e) => {
    e.preventDefault();

    let newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key])
        newErrors[key] = `${key.replace("_", " ")} is required`;
    });

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    // Add automatic fields
    const payload = {
      ...formData,
      status: "Pending",
      submitted_at: new Date().toISOString(),
    };

    try {
      await axios.post(
        "http://localhost:8081/Complaint-chain/api/complaints/submit",
        payload
      );

      setSuccessMessage("Complaint submitted successfully!");
      setFormData({
        name: "",
        email: "",
        contact: "",
        address: "",
        department: "",
        description: "",
        national_id: "",
        urgency: "",
      });
      setErrors({});
      fetchComplaints(); // refresh table

      setTimeout(() => setSuccessMessage(""), 2000);
    } catch (error) {
      const backendMsg = error.response?.data?.message || "Submission failed!";
      const err = {};
      if (backendMsg.toLowerCase().includes("email")) err.email = backendMsg;
      else if (backendMsg.toLowerCase().includes("national_id"))
        err.national_id = backendMsg;
      else err.general = backendMsg;
      setErrors(err);
      setTimeout(() => setErrors({}), 3000);
    }
  };

  return (
    <div className={styles.loginPageContainer}>
      <div className={styles.loginPageRight}>
        <div className={styles.loginPageBox}>
          <h2>Submit Complaint</h2>

          {errors.general && (
            <div className={styles.errorMessage}>{errors.general}</div>
          )}
          {successMessage && (
            <div className={styles.popupOverlay}>
              <div className={styles.successMessagePopup}>{successMessage}</div>
            </div>
          )}

          <form onSubmit={handleSubmitComplaint}>
            {Object.keys(formData).map((field) => (
              <div key={field} className={styles.loginPageFormGroup}>
                <label htmlFor={field}>
                  {field.charAt(0).toUpperCase() +
                    field.slice(1).replace("_", " ")}
                </label>
                <div className={styles.inputWithIcon}>
                  <input
                    type={field === "email" ? "email" : "text"}
                    id={field}
                    value={formData[field]}
                    onChange={handleChange}
                  />
                  {errors[field] && (
                    <span
                      className={styles.errorIcon}
                      data-tooltip={errors[field]}
                    ></span>
                  )}
                </div>
              </div>
            ))}

            <div className={styles.loginPageFormGroupFull}>
              <button className={styles.loginPageButton} type="submit">
                Submit Complaint
              </button>
            </div>
          </form>
        </div>

        {/* Complaints Table */}
        <div style={{ marginTop: "30px", width: "100%" }}>
          <h3>Your Complaints</h3>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginTop: "10px",
              color: "#fff",
            }}
          >
            <thead>
              <tr>
                <th style={{ border: "1px solid #555", padding: "8px" }}>
                  ID
                </th>
                <th style={{ border: "1px solid #555", padding: "8px" }}>
                  Name
                </th>
                <th style={{ border: "1px solid #555", padding: "8px" }}>
                  Department
                </th>
                <th style={{ border: "1px solid #555", padding: "8px" }}>
                  Description
                </th>
                <th style={{ border: "1px solid #555", padding: "8px" }}>
                  Status
                </th>
                <th style={{ border: "1px solid #555", padding: "8px" }}>
                  Submitted At
                </th>
              </tr>
            </thead>
            <tbody>
              {complaints.map((c) => (
                <tr key={c.id}>
                  <td style={{ border: "1px solid #555", padding: "8px" }}>
                    {c.id}
                  </td>
                  <td style={{ border: "1px solid #555", padding: "8px" }}>
                    {c.name}
                  </td>
                  <td style={{ border: "1px solid #555", padding: "8px" }}>
                    {c.department}
                  </td>
                  <td style={{ border: "1px solid #555", padding: "8px" }}>
                    {c.description}
                  </td>
                  <td style={{ border: "1px solid #555", padding: "8px" }}>
                    {c.status}
                  </td>
                  <td style={{ border: "1px solid #555", padding: "8px" }}>
                    {new Date(c.submitted_at).toLocaleString()}
                  </td>
                </tr>
              ))}
              {complaints.length === 0 && (
                <tr>
                  <td
                    colSpan={6}
                    style={{ border: "1px solid #555", textAlign: "center" }}
                  >
                    No complaints submitted yet.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
