import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AuthorityDashboard.css";

const AuthorityDashboard = ({ authorityId }) => {
  const [activeTab, setActiveTab] = useState("assigned");
  const [userData, setUserData] = useState({ firstName: "", lastName: "", email: "", nic: "", phone: "" });
  const [assignedComplaints, setAssignedComplaints] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [updatePopup, setUpdatePopup] = useState({ visible: false, complaint: null, status: "" });
  const [logoutPopup, setLogoutPopup] = useState(false);
  const [filterStatus, setFilterStatus] = useState("pending"); // pending / inprogress / completed

  // Fetch authority data
  useEffect(() => {
    if (!authorityId) return setError("No authority ID provided.");
    axios.get(`http://localhost:8081/Complaint-chain/api/auth/authority?id=${authorityId}`)
      .then(res => setUserData(res.data))
      .catch(() => setError("❌ Failed to fetch authority details."));
  }, [authorityId]);

  // Load assigned complaints
  useEffect(() => {
    loadAssignedComplaints(filterStatus);
  }, [filterStatus]);

  const loadAssignedComplaints = (status) => {
    axios.get(`http://localhost:8081/Complaint-chain/api/complaints/assigned/${authorityId}?status=${status}`)
      .then(res => setAssignedComplaints(res.data))
      .catch(() => setError("❌ Failed to load assigned complaints."));
  };

  const handleStatusChange = (e) => {
    const { value } = e.target;
    setUpdatePopup(prev => ({ ...prev, status: value }));
  };

  const handleUpdateStatus = () => {
    const { complaint, status } = updatePopup;
    if (!status) { setError("❌ Please select a status."); setTimeout(() => setError(""), 2500); return; }

    axios.put(`http://localhost:8081/Complaint-chain/api/complaints/update-status/${complaint.id}`, { status })
      .then(() => {
        setAssignedComplaints(prev => prev.map(c => c.id === complaint.id ? { ...c, status } : c));
        setUpdatePopup({ visible: false, complaint: null, status: "" });
        setMessage("✅ Complaint status updated successfully!");
        setTimeout(() => setMessage(""), 2500);
      })
      .catch(() => { setError("❌ Failed to update complaint."); setTimeout(() => setError(""), 2500); });
  };

  const handleRowClick = (complaint) => setUpdatePopup({ visible: true, complaint, status: complaint.status });

  const handleLogout = () => {
    localStorage.removeItem("authorityEmail");
    window.location.href = "/";
  };

  return (
    <div className="auth-home-page">
      <div className="auth-home-container">
        <h1>Authority Dashboard</h1>
        {error && <div className="auth-error">{error}</div>}
        {message && <div className="popupOverlay"><div className="successMessagePopup">{message}</div></div>}

        {/* Update Status Popup */}
        {updatePopup.visible && (
          <div className="popupOverlay">
            <div className="updatePopup">
              <h3>Update Complaint Status</h3>
              <p><strong>{updatePopup.complaint.description}</strong></p>
              <select value={updatePopup.status} onChange={handleStatusChange}>
                <option value="pending">Pending</option>
                <option value="inprogress">In Progress</option>
                <option value="completed">Completed</option>
              </select>
              <div className="popupButtons">
                <button onClick={handleUpdateStatus}>Update</button>
                <button onClick={() => setUpdatePopup({ visible: false, complaint: null, status: "" })}>Cancel</button>
              </div>
            </div>
          </div>
        )}

        {/* Tabs */}
        <div className="auth-btn-group">
          <button className={`auth-btn ${activeTab==="assigned"?"auth-btn-active":""}`} onClick={()=>setActiveTab("assigned")}>Assigned Complaints</button>
          <button className={`auth-btn ${activeTab==="profile"?"auth-btn-active":""}`} onClick={()=>setActiveTab("profile")}>Profile</button>
        </div>

        {/* Assigned Complaints Tab */}
        {activeTab==="assigned" && (
          <div className="auth-tab-box">
            <h3>Assigned Complaints</h3>
            <div className="filterContainer">
              <label>Status: </label>
              <select value={filterStatus} onChange={(e)=>setFilterStatus(e.target.value)}>
                <option value="pending">Pending</option>
                <option value="inprogress">In Progress</option>
                <option value="completed">Completed</option>
              </select>
            </div>
            <table className="auth-table">
              <thead>
                <tr><th>ID</th><th>Department</th><th>Priority</th><th>Description</th><th>Submission Date</th><th>Status</th></tr>
              </thead>
              <tbody>
                {assignedComplaints.map(c=>(
                  <tr key={c.id} onClick={()=>handleRowClick(c)}>
                    <td>{c.id}</td>
                    <td>{c.department}</td>
                    <td>{c.urgency}</td>
                    <td>{c.description}</td>
                    <td>{new Date(c.submittedAt).toLocaleString()}</td>
                    <td>{c.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Profile Tab */}
        {activeTab==="profile" && (
          <div className="auth-tab-box">
            <h3>Profile</h3>
            <table className="auth-table">
              <tbody>
                <tr><td>Full Name:</td><td>{userData.firstName} {userData.lastName}</td></tr>
                <tr><td>NIC:</td><td>{userData.nic}</td></tr>
                <tr><td>Email:</td><td>{userData.email}</td></tr>
                <tr><td>Phone:</td><td>{userData.phone}</td></tr>
              </tbody>
            </table>
            <div style={{marginTop:"20px", textAlign:"center"}}>
              <button onClick={()=>setLogoutPopup(true)} className="auth-btn-submit">Logout</button>
            </div>
            {logoutPopup && (
              <div className="popupOverlay">
                <div className="cancelPopup">
                  <h3>Confirm Logout</h3>
                  <p>Are you sure you want to logout?</p>
                  <div className="popupButtons">
                    <button onClick={handleLogout}>Yes, Logout</button>
                    <button onClick={()=>setLogoutPopup(false)}>Cancel</button>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

      </div>
    </div>
  );
};

export default AuthorityDashboard;
