import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // if using backend auth
import styles from "./Login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setErrorMessage("Please enter valid credentials.");
      return;
    }

    try {
      // Call your backend login endpoint
      const response = await axios.post("http://localhost:8081/Complaint-chain/api/auth/login", {
        email,
        password,
      });

      setSuccessMessage(`Welcome, ${response.data.firstName}`);
      setErrorMessage("");

      setTimeout(() => {
        setSuccessMessage("");
        navigate("/dashboard"); // redirect after popup
      }, 2000); // 2s for popup
    } catch (error) {
      setErrorMessage(
        error.response?.data?.message || "Login failed. Try again."
      );
      setSuccessMessage("");
      setTimeout(() => setErrorMessage(""), 3000);
    }
  };

  return (
    <div className={styles.loginPageContainer}>
      <div className={styles.loginPageLeft}>
        <div>
          <h1>Welcome Back, Citizen</h1>
          <p>Log in to access your portal, manage complaints, and stay updated.</p>
        </div>
      </div>

      <div className={styles.loginPageRight}>
        <div className={styles.loginPageBox}>
          <h2>Login</h2>

          {successMessage && (
            <div className={styles.popupMessageSuccess}>{successMessage}</div>
          )}
          {errorMessage && (
            <div className={styles.popupMessageError}>{errorMessage}</div>
          )}

          <form onSubmit={handleSubmit}>
            <div className={styles.loginPageFormGroup}>
              <label>Email</label>
              <input
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className={styles.loginPageFormGroup}>
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit" className={styles.loginPageButton}>
              Login
            </button>
          </form>

          <div className={styles.loginPageExtraLinks}>
            <p>
              Donâ€™t have an account? <a href="/register">Register</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
