import "./CustomerDashboard.css";

const CustomerDashboard = () => {
  const [activeTab, setActiveTab] = useState("submit");

  const [complaintData, setComplaintData] = useState({
    name: "",
    nationalId: "",
    contact: "",
    email: "",
    address: "",
    description: "",
    department: "Select Department",
    urgency: "High",
    submittedAt: new Date().toISOString().slice(0, 16),
  });

  const [complaints, setComplaints] = useState([
    {
      id: 1,
      name: "Jane Doe",
      department: "Health",
      urgency: "High",
      description: "Issue with hospital services",
      submittedAt: "2025-08-27T09:30",
      status: "In Progress",
    },
    {
      id: 2,
      name: "John Smith",
      department: "Transport",
      urgency: "Low",
      description: "Delayed bus service",
      submittedAt: "2025-08-25T14:00",
      status: "Resolved",
    },
    {
      id: 3,
      name: "Alice Brown",
      department: "Education",
      urgency: "High",
      description: "School facilities issue",
      submittedAt: "2025-08-26T11:15",
      status: "Pending",
    },
  ]);

  const [sortType, setSortType] = useState("date-asc");

  const [userData] = useState({
    name: "Jane Doe",
    email: "jane@example.com",
    phone: "+9876543210",
  });

  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setComplaintData({ ...complaintData, [name]: value });
  };

  // ---------------- Complaint Submission ----------------
  const handleSubmitComplaint = async (e) => {
    e.preventDefault();

    try {
      // Submit to backend
      const response = await axios.post(
        "http://localhost:8081/api/complaints/submit",
        complaintData
      );

      // Add returned complaint to state
      setComplaints([...complaints, response.data]);

      setSuccessMessage("Complaint submitted successfully!");
      setErrorMessage("");

      // Reset form
      setComplaintData({
        name: "",
        nationalId: "",
        contact: "",
        email: "",
        address: "",
        description: "",
        department: "Select Department",
        urgency: "High",
        submittedAt: new Date().toISOString().slice(0, 16),
      });

      // Hide success message after 3s
      setTimeout(() => setSuccessMessage(""), 3000);
    } catch (error) {
      console.error("Error submitting complaint:", error);
      setErrorMessage(
        error.response?.data?.message || "Failed to submit complaint."
      );
      setSuccessMessage("");

      setTimeout(() => setErrorMessage(""), 3000);
    }
  };

  const handleSortChange = (value) => setSortType(value);

  const sortedComplaints = [...complaints].sort((a, b) => {
    switch (sortType) {
      case "date-asc":
        return new Date(a.submittedAt) - new Date(b.submittedAt);
      case "date-desc":
        return new Date(b.submittedAt) - new Date(a.submittedAt);
      case "priority-asc":
        return a.urgency.localeCompare(b.urgency);
      case "priority-desc":
        return b.urgency.localeCompare(a.urgency);
      case "overall-asc":
        return (
          new Date(a.submittedAt) - new Date(b.submittedAt) ||
          a.urgency.localeCompare(b.urgency)
        );
      case "overall-desc":
        return (
          new Date(b.submittedAt) - new Date(a.submittedAt) ||
          b.urgency.localeCompare(a.urgency)
        );
      default:
        return 0;
    }
  });

  return (
    <div className="cust-home-page">
      <div className="cust-home-container">
        <h1>Customer Dashboard</h1>

        {/* Tabs */}
        <div className="cust-btn-group">
          <button
            className={`cust-btn ${activeTab === "submit" ? "cust-btn-active" : ""}`}
            onClick={() => setActiveTab("submit")}
          >
            Submit Complaint
          </button>
          <button
            className={`cust-btn ${activeTab === "track" ? "cust-btn-active" : ""}`}
            onClick={() => setActiveTab("track")}
          >
            Track Complaint
          </button>
          <button
            className={`cust-btn ${activeTab === "profile" ? "cust-btn-active" : ""}`}
            onClick={() => setActiveTab("profile")}
          >
            User Profile
          </button>
        </div>

        {/* Success / Error Popups */}
        {successMessage && (
          <div className="popupMessageSuccess">{successMessage}</div>
        )}
        {errorMessage && <div className="popupMessageError">{errorMessage}</div>}

        {/* Tab Content */}
        <div className="cust-tab-content">
          {/* Submit Complaint */}
          {activeTab === "submit" && (
            <div className="cust-tab-box">
              <h3>Submit Complaint</h3>
              <form onSubmit={handleSubmitComplaint} className="cust-form-grid">
                <div className="form-group">
                  <label>Name of Citizen:</label>
                  <input
                    type="text"
                    name="name"
                    placeholder="Enter Name"
                    value={complaintData.name}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>National ID Number:</label>
                  <input
                    type="text"
                    name="nationalId"
                    placeholder="Enter National ID"
                    value={complaintData.nationalId}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Contact Number:</label>
                  <input
                    type="text"
                    name="contact"
                    placeholder="Enter Contact Number"
                    value={complaintData.contact}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Email Address:</label>
                  <input
                    type="email"
                    name="email"
                    placeholder="Enter Email"
                    value={complaintData.email}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Address:</label>
                  <input
                    type="text"
                    name="address"
                    placeholder="Enter Address"
                    value={complaintData.address}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group full-width">
                  <label>Description:</label>
                  <textarea
                    name="description"
                    placeholder="Enter Complaint Description"
                    value={complaintData.description}
                    onChange={handleChange}
                    required
                    rows={5}
                  />
                </div>

                <div className="form-group">
                  <label>Department:</label>
                  <select
                    name="department"
                    value={complaintData.department}
                    onChange={handleChange}
                    required
                  >
                    <option disabled>Select Department</option>
                    <option>Health</option>
                    <option>Education</option>
                    <option>Transport</option>
                    <option>Utilities</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Priority:</label>
                  <select
                    name="urgency"
                    value={complaintData.urgency}
                    onChange={handleChange}
                    required
                  >
                    <option>High</option>
                    <option>Low</option>
                  </select>
                </div>

                <div className="form-group">
                  <label>Submission Date & Time:</label>
                  <input
                    type="datetime-local"
                    name="submittedAt"
                    value={complaintData.submittedAt}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group full-width" style={{ textAlign: "center" }}>
                  <button type="submit" className="cust-btn-submit">
                    Submit
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Track Complaint */}
          {activeTab === "track" && (
            <div className="cust-tab-box">
              <h3>Track Complaint</h3>

              <div
                style={{
                  display: "flex",
                  gap: "10px",
                  marginBottom: "12px",
                  flexWrap: "wrap",
                  alignItems: "center",
                }}
              >
                <label style={{ color: "#ddd" }}>Sort by:</label>
                <select onChange={(e) => handleSortChange(e.target.value)}>
                  <option value="date-asc">Date Ascending</option>
                  <option value="date-desc">Date Descending</option>
                  <option value="priority-asc">Priority Low → High</option>
                  <option value="priority-desc">Priority High → Low</option>
                  <option value="overall-asc">Overall Ascending</option>
                  <option value="overall-desc">Overall Descending</option>
                </select>
              </div>

              <table className="cust-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Department</th>
                    <th>Priority</th>
                    <th>Description</th>
                    <th>Submission Date & Time</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {sortedComplaints.map((c) => (
                    <tr key={c.id}>
                      <td>{c.id}</td>
                      <td>{c.name}</td>
                      <td>{c.department}</td>
                      <td>{c.urgency}</td>
                      <td>{c.description}</td>
                      <td>{c.submittedAt}</td>
                      <td>{c.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* User Profile */}
          {activeTab === "profile" && (
            <div className="cust-tab-box">
              <h3>User Profile</h3>
              <div className="cust-profile">
                <p>
                  <strong>Name:</strong> {userData.name}
                </p>
                <p>
                  <strong>Email:</strong> {userData.email}
                </p>
                <p>
                  <strong>Phone:</strong> {userData.phone}
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
