import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "./Login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (email && password) {
      // Show success popup
      setSuccessMessage("Login successful! Redirecting...");
      setErrorMessage("");

      // Auto-clear and redirect
      setTimeout(() => {
        setSuccessMessage("");
        navigate("/dashboard");
      }, 1000); // fast transition
    } else {
      setErrorMessage("Please enter valid credentials.");
      setSuccessMessage("");
      setTimeout(() => setErrorMessage(""), 3000); // auto-hide
    }
  };

  return (
    <div className={styles.loginPageContainer}>
      {/* Left Side */}
      <div className={styles.loginPageLeft}>
        <div>
          <h1>Welcome Back, Citizen</h1>
          <p>
            Log in to access your portal, manage complaints, and stay updated
            on services that matter to you.
          </p>
        </div>
      </div>

      {/* Right Side */}
      <div className={styles.loginPageRight}>
        <div className={styles.loginPageBox}>
          <h2>Login</h2>

          {/* Popup messages */}
          {errorMessage && (
            <div className={styles.popupMessageError}>{errorMessage}</div>
          )}
          {successMessage && (
            <div className={styles.popupMessageSuccess}>{successMessage}</div>
          )}

          <form onSubmit={handleSubmit}>
            <div className={styles.loginPageFormGroup}>
              <label htmlFor="email">Email</label>
              <input
                type="text"
                id="email"
                placeholder="Enter your email or username"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>

            <div className={styles.loginPageFormGroup}>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>

            <button type="submit" className={styles.loginPageButton}>
              Login
            </button>

            <div className={styles.loginPageExtraLinks}>
              <p>
                <a href="#">Forgot Password?</a>
              </p>
              <p>
                Don’t have an account? <a href="/register">Register</a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
