import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import styles from "./Login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setErrorMessage("Please enter both email and password");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8081/Complaint-chain/api/auth/login",
        { email, password }
      );

      // Assuming response.data has { firstName, token, role }
      setSuccessMessage(`Welcome ${response.data.firstName}! Redirecting...`);
      setErrorMessage("");

      setTimeout(() => {
        setSuccessMessage("");
        navigate("/dashboard");
      }, 1500); // shorter transition
    } catch (error) {
      if (error.response && error.response.data) {
        setErrorMessage(error.response.data.message || "Login failed");
      } else {
        setErrorMessage("Network error. Please try again.");
      }
    }
  };

  return (
    <div className={styles.loginPageContainer}>
      {/* Left Side */}
      <div className={styles.loginPageLeft}>
        <div>
          <h1>Welcome Back, Citizen</h1>
          <p>
            Log in to access your portal, manage complaints, and stay updated
            on services that matter to you.
          </p>
        </div>
      </div>

      {/* Right Side */}
      <div className={styles.loginPageRight}>
        <div className={styles.loginPageBox}>
          <h2>Login</h2>

          {errorMessage && <div className={styles.errorMessage}>{errorMessage}</div>}
          {successMessage && <div className={styles.successMessage}>{successMessage}</div>}

          <form onSubmit={handleSubmit}>
            <div className={styles.loginPageFormGroup}>
              <label htmlFor="email">Email</label>
              <input
                type="text"
                id="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className={styles.loginPageFormGroup}>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit" className={styles.loginPageButton}>
              Login
            </button>
            <div className={styles.loginPageExtraLinks}>
              <p>
                <a href="#">Forgot Password?</a>
              </p>
              <p>
                Don’t have an account? <a href="/register">Register</a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
