import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import styles from "./Login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
    setErrors({ ...errors, [e.target.id]: "" }); // clear field error while typing
  };

  const validateForm = () => {
    let newErrors = {};
    if (!formData.email) newErrors.email = "Email is required";
    if (!formData.password) newErrors.password = "Password is required";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = validateForm();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      // Replace with your backend login endpoint
      const response = await axios.post(
        "http://localhost:8081/Complaint-chain/api/auth/login",
        formData
      );

      setSuccessMessage(`Welcome, ${response.data.firstName}`);
      setErrors({});

      setTimeout(() => {
        setSuccessMessage("");
        navigate("/dashboard");
      }, 2000); // redirect after popup
    } catch (error) {
      const backendMsg = error.response?.data?.message || "Login failed!";
      setErrors({ general: backendMsg });
      setSuccessMessage("");
    }
  };

  return (
    <div className={styles.loginPageContainer}>
      <div className={styles.loginPageLeft}>
        <div>
          <h1>Welcome Back, Citizen</h1>
          <p>Log in to access your portal, manage complaints, and stay updated.</p>
        </div>
      </div>

      <div className={styles.loginPageRight}>
        <div className={styles.loginPageBox}>
          <h2>Login</h2>

          {errors.general && <div className={styles.errorMessage}>{errors.general}</div>}
          {successMessage && <div className={styles.successMessage}>{successMessage}</div>}

          <form className={styles.registerPageForm} onSubmit={handleSubmit}>
            {["email", "password"].map((field) => (
              <div key={field} className={styles.registerPageFormGroup}>
                <label htmlFor={field}>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
                <div className={styles.inputWithIcon}>
                  <input
                    type={field === "password" ? "password" : "text"}
                    id={field}
                    value={formData[field]}
                    onChange={handleChange}
                  />
                  {errors[field] && (
                    <span className={styles.errorIcon} data-tooltip={errors[field]}></span>
                  )}
                </div>
              </div>
            ))}

            <div className={`${styles.registerPageFormGroup} ${styles.registerPageFullWidth}`}>
              <button className={styles.registerPageButton} type="submit">Login</button>
            </div>

            <div className={styles.registerPageExtraLinks}>
              <p>
                Donâ€™t have an account? <a href="/register">Register</a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
