import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [adminData, setAdminData] = useState({
    fullName: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });

  const [users, setUsers] = useState([]);
  const [complaints, setComplaints] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  // Fetch admin profile & users
  useEffect(() => {
    const email = localStorage.getItem("userEmail");
    if (!email) return setError("No email found in localStorage. Please log in.");

    // Admin profile
    axios.get(`http://localhost:8081/Complaint-chain/api/auth/admin?email=${email}`)
      .then(res => setAdminData({ ...res.data, password: "", confirmPassword: "" }))
      .catch(() => setError("❌ Failed to fetch admin profile."));

    // Load users
    loadUsers();
    // Load complaints
    loadComplaints();
  }, []);

  const loadUsers = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/users")
      .then(res => setUsers(res.data))
      .catch(() => setError("❌ Failed to load users."));
  };

  const loadComplaints = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/complaints/all")
      .then(res => setComplaints(res.data))
      .catch(() => setError("❌ Failed to load complaints."));
  };

  const handleAdminChange = (e) => {
    const { name, value } = e.target;
    setAdminData({ ...adminData, [name]: value });
  };

  const handleProfileUpdate = (e) => {
    e.preventDefault();
    if (adminData.password && adminData.password !== adminData.confirmPassword) {
      setError("❌ Passwords do not match!");
      setTimeout(() => setError(""), 2500);
      return;
    }

    const payload = { ...adminData };
    axios.put("http://localhost:8081/Complaint-chain/api/auth/admin/update", payload)
      .then(() => {
        setMessage("✅ Profile updated successfully!");
        setTimeout(() => setMessage(""), 2500);
      })
      .catch(() => {
        setError("❌ Failed to update profile.");
        setTimeout(() => setError(""), 2500);
      });
  };

  const handleUserRoleChange = (userId, newRole) => {
    axios.put(`http://localhost:8081/Complaint-chain/api/users/${userId}/role`, { role: newRole })
      .then(() => loadUsers())
      .catch(() => setError("❌ Failed to update user role."));
  };

  const toggleUserStatus = (user) => {
    axios.put(`http://localhost:8081/Complaint-chain/api/users/${user.id}/toggleStatus`)
      .then(() => loadUsers())
      .catch(() => setError("❌ Failed to update user status."));
  };

  return (
    <div className="admin-home-page">
      <div className="admin-home-container">
        <h1>Admin Dashboard</h1>
        {error && <div className="cust-error">{error}</div>}
        {message && <div className="popupOverlay"><div className="successMessagePopup">{message}</div></div>}

        <div className="cust-btn-group">
          <button className={`cust-btn ${activeTab==="dashboard"?"cust-btn-active":""}`} onClick={()=>setActiveTab("dashboard")}>Dashboard</button>
          <button className={`cust-btn ${activeTab==="profile"?"cust-btn-active":""}`} onClick={()=>setActiveTab("profile")}>Profile</button>
          <button className={`cust-btn ${activeTab==="users"?"cust-btn-active":""}`} onClick={()=>setActiveTab("users")}>Manage Users</button>
          <button className={`cust-btn ${activeTab==="complaints"?"cust-btn-active":""}`} onClick={()=>setActiveTab("complaints")}>Complaints</button>
        </div>

        {/* Dashboard Summary */}
        {activeTab==="dashboard" && (
          <div className="cust-tab-box">
            <h3>Welcome, {adminData.fullName}</h3>
            <p>Total Users: {users.length}</p>
            <p>Total Complaints: {complaints.length}</p>
          </div>
        )}

        {/* Profile Tab */}
        {activeTab === "profile" && (
          <div className="cust-tab-box">
            <h3>Admin Profile</h3>
            <form onSubmit={handleProfileUpdate} className="cust-form-grid">
              <div className="form-group full-width">
                <label>Full Name:</label>
                <input type="text" name="fullName" value={adminData.fullName} onChange={handleAdminChange} />
              </div>
              <div className="form-group">
                <label>Email:</label>
                <input type="email" name="email" value={adminData.email} onChange={handleAdminChange} />
              </div>
              <div className="form-group">
                <label>Phone:</label>
                <input type="text" name="phone" value={adminData.phone} onChange={handleAdminChange} />
              </div>
              <div className="form-group">
                <label>Password:</label>
                <input type="password" name="password" value={adminData.password} onChange={handleAdminChange} />
              </div>
              <div className="form-group">
                <label>Confirm Password:</label>
                <input type="password" name="confirmPassword" value={adminData.confirmPassword} onChange={handleAdminChange} />
              </div>
              <div className="form-group full-width" style={{ textAlign: "center" }}>
                <button type="submit" className="cust-btn-submit">Update Profile</button>
              </div>
            </form>
          </div>
        )}

        {/* Manage Users Tab */}
        {activeTab === "users" && (
          <div className="cust-tab-box">
            <h3>Manage Users</h3>
            <table className="cust-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.fullName}</td>
                    <td>{user.email}</td>
                    <td>
                      <select value={user.role} onChange={(e)=>handleUserRoleChange(user.id, e.target.value)}>
                        <option>Citizen</option>
                        <option>Staff</option>
                        <option>Manager</option>
                        <option>Admin</option>
                      </select>
                    </td>
                    <td>{user.active ? "Active" : "Inactive"}</td>
                    <td><button onClick={()=>toggleUserStatus(user)}>{user.active ? "Deactivate" : "Activate"}</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Complaints Tab */}
        {activeTab === "complaints" && (
          <div className="cust-tab-box">
            <h3>All Complaints</h3>
            <table className="cust-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>User</th>
                  <th>Department</th>
                  <th>Description</th>
                  <th>Priority</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {complaints.map(c => (
                  <tr key={c.id}>
                    <td>{c.id}</td>
                    <td>{c.userName}</td>
                    <td>{c.department}</td>
                    <td>{c.description}</td>
                    <td>{c.urgency}</td>
                    <td>{c.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

      </div>
    </div>
  );
};

export default AdminDashboard;
