import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("createAuthority");
  const [authorityData, setAuthorityData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    role: "Select Role",
    phone: "",
  });
  const [authorityList, setAuthorityList] = useState([]);
  const [complaints, setComplaints] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [fieldErrors, setFieldErrors] = useState({});

  // Load all complaints
  useEffect(() => {
    if (activeTab === "viewComplaints") loadComplaints();
    if (activeTab === "authorityList") loadAuthorities();
  }, [activeTab]);

  const handleChangeAuthority = (e) => {
    const { name, value } = e.target;
    setAuthorityData({ ...authorityData, [name]: value });
    setFieldErrors({ ...fieldErrors, [name]: "" });
  };

  const handleCreateAuthority = (e) => {
    e.preventDefault();
    setMessage(""); setError("");
    let errors = {};
    if (!authorityData.firstName) errors.firstName = "First name required";
    if (!authorityData.lastName) errors.lastName = "Last name required";
    if (!authorityData.email) errors.email = "Email required";
    if (authorityData.role === "Select Role") errors.role = "Select a role";

    if (Object.keys(errors).length > 0) { setFieldErrors(errors); return; }

    axios.post("http://localhost:8081/Complaint-chain/api/admin/authority", authorityData)
      .then(res => {
        setMessage("✅ Authority profile created successfully!");
        setAuthorityData({ firstName: "", lastName: "", email: "", role: "Select Role", phone: "" });
        setFieldErrors({});
        loadAuthorities();
        setTimeout(() => setMessage(""), 2500);
      })
      .catch(err => {
        console.error(err.response?.data || err.message);
        setError("❌ Failed to create authority profile.");
        setTimeout(() => setError(""), 2500);
      });
  };

  const loadComplaints = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/complaints/all")
      .then(res => setComplaints(res.data))
      .catch(() => setError("❌ Failed to load complaints."));
  };

  const loadAuthorities = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/admin/authorities")
      .then(res => setAuthorityList(res.data))
      .catch(() => setError("❌ Failed to load authorities."));
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      {error && <div className="error">{error}</div>}
      {message && <div className="success">{message}</div>}

      {/* Tabs */}
      <div className="tab-buttons">
        <button className={activeTab==="createAuthority"?"active":""} onClick={()=>setActiveTab("createAuthority")}>Create Authority</button>
        <button className={activeTab==="viewComplaints"?"active":""} onClick={()=>setActiveTab("viewComplaints")}>View Complaints</button>
        <button className={activeTab==="authorityList"?"active":""} onClick={()=>setActiveTab("authorityList")}>Authority List</button>
      </div>

      {/* Create Authority Tab */}
      {activeTab==="createAuthority" && (
        <div className="tab-content">
          <h3>Create Authority Profile</h3>
          <form onSubmit={handleCreateAuthority} className="form-grid">
            {["firstName","lastName","email","phone"].map(f=>(
              <div className="form-group" key={f}>
                <label>{f.replace(/([A-Z])/g," $1")}</label>
                <input type="text" name={f} value={authorityData[f]} onChange={handleChangeAuthority}/>
                {fieldErrors[f] && <span className="errorMessage">{fieldErrors[f]}</span>}
              </div>
            ))}
            <div className="form-group">
              <label>Role</label>
              <select name="role" value={authorityData.role} onChange={handleChangeAuthority}>
                <option disabled>Select Role</option>
                <option>Department Head</option>
                <option>Clerk</option>
                <option>Manager</option>
              </select>
              {fieldErrors.role && <span className="errorMessage">{fieldErrors.role}</span>}
            </div>
            <div className="form-group full-width" style={{textAlign:"center"}}>
              <button type="submit" className="btn-submit">Create</button>
            </div>
          </form>
        </div>
      )}

      {/* View Complaints Tab */}
      {activeTab==="viewComplaints" && (
        <div className="tab-content">
          <h3>All Complaints</h3>
          <table className="table">
            <thead>
              <tr><th>ID</th><th>Department</th><th>Priority</th><th>Description</th><th>Status</th><th>Submitted At</th></tr>
            </thead>
            <tbody>
              {complaints.map(c=>(
                <tr key={c.id}>
                  <td>{c.id}</td>
                  <td>{c.department}</td>
                  <td>{c.urgency}</td>
                  <td>{c.description}</td>
                  <td>{c.status}</td>
                  <td>{new Date(c.submittedAt).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Authority List Tab */}
      {activeTab==="authorityList" && (
        <div className="tab-content">
          <h3>Authority List</h3>
          <table className="table">
            <thead>
              <tr><th>ID</th><th>Name</th><th>Email</th><th>Phone</th><th>Role</th></tr>
            </thead>
            <tbody>
              {authorityList.map(a=>(
                <tr key={a.id}>
                  <td>{a.id}</td>
                  <td>{a.firstName} {a.lastName}</td>
                  <td>{a.email}</td>
                  <td>{a.phone}</td>
                  <td>{a.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

    </div>
  );
};

export default AdminDashboard;
