import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AuthorityDashboard.css"; // Make sure it contains the theme you provided

const AuthorityDashboard = () => {
  const [activeTab, setActiveTab] = useState("assigned");
  const [userData, setUserData] = useState({ id: "", name: "", email: "", department: "" });
  const [assignedComplaints, setAssignedComplaints] = useState([]);
  const [resolvedComplaints, setResolvedComplaints] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [logoutPopup, setLogoutPopup] = useState(false);

  // Fetch authority details
  useEffect(() => {
    const email = localStorage.getItem("authorityEmail");
    if (!email) return setError("No email found. Please log in.");

    axios
      .get(`http://localhost:8081/Complaint-chain/api/authority/findByEmail?email=${email}`)
      .then((res) => setUserData(res.data))
      .catch(() => setError("❌ Failed to fetch authority details."));
  }, []);

  // Load complaints when tab changes
  useEffect(() => {
    if (activeTab === "assigned") loadAssignedComplaints();
    if (activeTab === "resolved") loadResolvedComplaints();
  }, [activeTab]);

  const loadAssignedComplaints = () => {
    const email = localStorage.getItem("authorityEmail");
    if (!email) return setError("No email found. Please log in.");

    axios
      .get(`http://localhost:8081/Complaint-chain/api/tasks/complaints-by-authority?email=${email}`)
      .then((res) => setAssignedComplaints(res.data))
      .catch(() => setError("❌ Failed to load assigned complaints."));
  };

  const loadResolvedComplaints = () => {
    axios
      .get("http://localhost:8081/Complaint-chain/api/complaints/resolved")
      .then((res) => setResolvedComplaints(res.data))
      .catch(() => setError("❌ Failed to load resolved complaints."));
  };

  // Mark complaint as resolved
  const handleResolve = (id) => {
    axios
      .post(`http://localhost:8081/Complaint-chain/api/complaints/resolve/${id}`)
      .then(() => {
        setMessage("✅ Complaint marked as resolved!");
        setAssignedComplaints(assignedComplaints.filter((c) => c.id !== id));
        loadResolvedComplaints();
        setTimeout(() => setMessage(""), 2500);
      })
      .catch(() => setError("❌ Failed to resolve complaint."));
  };

  const handleLogout = () => {
    localStorage.removeItem("authorityEmail");
    window.location.href = "/";
  };

  return (
    <div className="auth-home-page">
      <div className="auth-home-container">
        <h1>Authority Dashboard</h1>

        {error && <div className="auth-error">{error}</div>}

        {message && (
          <div className="popupOverlay">
            <div className="successMessagePopup">{message}</div>
          </div>
        )}

        {/* Tabs */}
        <div className="auth-btn-group">
          <button
            className={`auth-btn ${activeTab === "assigned" ? "auth-btn-active" : ""}`}
            onClick={() => setActiveTab("assigned")}
          >
            Assigned Complaints
          </button>
          <button
            className={`auth-btn ${activeTab === "resolved" ? "auth-btn-active" : ""}`}
            onClick={() => setActiveTab("resolved")}
          >
            Resolved Complaints
          </button>
          <button
            className={`auth-btn ${activeTab === "profile" ? "auth-btn-active" : ""}`}
            onClick={() => setActiveTab("profile")}
          >
            Profile
          </button>
        </div>

        {/* Assigned Complaints */}
        {activeTab === "assigned" && (
          <div className="auth-tab-box">
            <h3>Assigned Complaints</h3>
            <table className="auth-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Citizen</th>
                  <th>Department</th>
                  <th>Priority</th>
                  <th>Description</th>
                  <th>Submitted At</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {assignedComplaints.map((c) => (
                  <tr key={c.id}>
                    <td>{c.id}</td>
                    <td>{c.name}</td>
                    <td>{c.department}</td>
                    <td>{c.urgency}</td>
                    <td>{c.description}</td>
                    <td>{new Date(c.submittedAt).toLocaleString()}</td>
                    <td>
                      <button className="auth-btn-submit" onClick={() => handleResolve(c.id)}>
                        Resolve
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Resolved Complaints */}
        {activeTab === "resolved" && (
          <div className="auth-tab-box">
            <h3>Resolved Complaints</h3>
            <table className="auth-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Citizen</th>
                  <th>Department</th>
                  <th>Description</th>
                  <th>Resolved At</th>
                </tr>
              </thead>
              <tbody>
                {resolvedComplaints.map((c) => (
                  <tr key={c.id}>
                    <td>{c.id}</td>
                    <td>{c.name}</td>
                    <td>{c.department}</td>
                    <td>{c.description}</td>
                    <td>{new Date(c.resolvedAt).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Profile */}
        {activeTab === "profile" && (
          <div className="auth-tab-box">
            <h3>Authority Profile</h3>
            <table className="auth-table">
              <tbody>
                <tr>
                  <td>ID:</td>
                  <td>{userData.id}</td>
                </tr>
                <tr>
                  <td>Name:</td>
                  <td>{userData.name}</td>
                </tr>
                <tr>
                  <td>Email:</td>
                  <td>{userData.email}</td>
                </tr>
                <tr>
                  <td>Department:</td>
                  <td>{userData.department}</td>
                </tr>
              </tbody>
            </table>
            <div style={{ marginTop: "20px", textAlign: "center" }}>
              <button onClick={() => setLogoutPopup(true)} className="auth-btn-submit">
                Logout
              </button>
            </div>

            {logoutPopup && (
              <div className="popupOverlay">
                <div className="updatePopup">
                  <h3>Confirm Logout</h3>
                  <p>Are you sure you want to logout?</p>
                  <div className="popupButtons">
                    <button onClick={handleLogout} className="auth-btn-submit">
                      Yes, Logout
                    </button>
                    <button onClick={() => setLogoutPopup(false)} className="auth-btn">
                      Cancel
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AuthorityDashboard;
