import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("allComplaints");
  const [complaints, setComplaints] = useState([]);
  const [cancelledComplaints, setCancelledComplaints] = useState([]);
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [updatePopup, setUpdatePopup] = useState({ visible: false, complaint: null });
  const [logoutPopup, setLogoutPopup] = useState(false);
  const [filter, setFilter] = useState("high-low");
  const [statusFilter, setStatusFilter] = useState("pending");

  useEffect(() => {
    if (activeTab === "allComplaints") loadComplaints();
    if (activeTab === "cancelled") loadCancelledComplaints();
    if (activeTab === "users") loadUsers();
  }, [activeTab, filter, statusFilter]);

  const loadComplaints = () => {
    axios.get(`http://localhost:8081/Complaint-chain/api/complaints/${statusFilter}?sort=${filter}`)
      .then(res => setComplaints(res.data))
      .catch(() => setError("❌ Failed to load complaints."));
  };

  const loadCancelledComplaints = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/complaints/cancelled")
      .then(res => setCancelledComplaints(res.data))
      .catch(() => setError("❌ Failed to load cancelled complaints."));
  };

  const loadUsers = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/auth/all-citizens")
      .then(res => setUsers(res.data))
      .catch(() => setError("❌ Failed to load users."));
  };

  const handleLogout = () => {
    localStorage.removeItem("userEmail");
    window.location.href = "/";
  };

  const handleUpdateStatus = (complaint, newStatus) => {
    axios.put(`http://localhost:8081/Complaint-chain/api/complaints/update/${complaint.id}?status=${newStatus}`)
      .then(() => {
        setMessage("✅ Status updated successfully!");
        loadComplaints();
        setTimeout(() => setMessage(""), 2500);
        setUpdatePopup({ visible: false, complaint: null });
      })
      .catch(() => {
        setError("❌ Failed to update status.");
        setTimeout(() => setError(""), 2500);
      });
  };

  return (
    <div className="cust-home-page">
      <div className="cust-home-container">
        <h1>Admin Dashboard</h1>
        {error && <div className="cust-error">{error}</div>}
        {message && <div className="popupOverlay"><div className="successMessagePopup">{message}</div></div>}

        {/* Logout Popup */}
        {logoutPopup && (
          <div className="popupOverlay">
            <div className="cancelPopup">
              <h3>Confirm Logout</h3>
              <p>Are you sure you want to logout?</p>
              <div className="popupButtons">
                <button onClick={handleLogout}>Yes, Logout</button>
                <button onClick={() => setLogoutPopup(false)}>Cancel</button>
              </div>
            </div>
          </div>
        )}

        {/* Tabs */}
        <div className="cust-btn-group">
          <button className={`cust-btn ${activeTab === "allComplaints" ? "cust-btn-active" : ""}`} onClick={() => setActiveTab("allComplaints")}>All Complaints</button>
          <button className={`cust-btn ${activeTab === "cancelled" ? "cust-btn-active" : ""}`} onClick={() => setActiveTab("cancelled")}>Cancelled Complaints</button>
          <button className={`cust-btn ${activeTab === "users" ? "cust-btn-active" : ""}`} onClick={() => setActiveTab("users")}>Users</button>
          <button className={`cust-btn`} onClick={() => setLogoutPopup(true)}>Logout</button>
        </div>

        {/* All Complaints Tab */}
        {activeTab === "allComplaints" && (
          <div className="cust-tab-box">
            <h3>All Complaints</h3>
            <div className="filterContainer">
              <label>Status: </label>
              <select value={statusFilter} onChange={e => setStatusFilter(e.target.value)}>
                <option value="pending">Pending</option>
                <option value="inprogress">In Progress</option>
                <option value="resolved">Resolved</option>
              </select>
              <label>Sort by: </label>
              <select value={filter} onChange={e => setFilter(e.target.value)}>
                <option value="high-low">Priority: High → Low</option>
                <option value="low-high">Priority: Low → High</option>
                <option value="date-asc">Date: Ascending</option>
                <option value="date-desc">Date: Descending</option>
              </select>
            </div>
            <table className="cust-table">
              <thead>
                <tr><th>ID</th><th>Department</th><th>Priority</th><th>Description</th><th>Submitted</th><th>Status</th><th>Action</th></tr>
              </thead>
              <tbody>
                {complaints.map(c => (
                  <tr key={c.id} style={{ backgroundColor: c.status === "In Progress" ? "#fff3cd" : "" }}>
                    <td>{c.id}</td>
                    <td>{c.department}</td>
                    <td>{c.urgency}</td>
                    <td>{c.description}</td>
                    <td>{new Date(c.submittedAt).toLocaleString()}</td>
                    <td>{c.status}</td>
                    <td>
                      <button onClick={() => setUpdatePopup({ visible: true, complaint: c })}>Update Status</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Update Status Popup */}
            {updatePopup.visible && (
              <div className="popupOverlay">
                <div className="cancelPopup">
                  <h3>Update Status</h3>
                  <p>Complaint: <strong>{updatePopup.complaint.description}</strong></p>
                  <div className="popupButtons">
                    <button onClick={() => handleUpdateStatus(updatePopup.complaint, "In Progress")}>In Progress</button>
                    <button onClick={() => handleUpdateStatus(updatePopup.complaint, "Resolved")}>Resolved</button>
                    <button onClick={() => setUpdatePopup({ visible: false, complaint: null })}>Cancel</button>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Cancelled Complaints Tab */}
        {activeTab === "cancelled" && (
          <div className="cust-tab-box">
            <h3>Cancelled Complaints</h3>
            <table className="cust-table">
              <thead>
                <tr><th>ID</th><th>Department</th><th>Priority</th><th>Description</th><th>Reason</th><th>Cancelled At</th></tr>
              </thead>
              <tbody>
                {cancelledComplaints.map(c => (
                  <tr key={c.id}>
                    <td>{c.originalId}</td>
                    <td>{c.department}</td>
                    <td>{c.urgency}</td>
                    <td>{c.description}</td>
                    <td>{c.reason}</td>
                    <td>{new Date(c.submittedAt).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Users Tab */}
        {activeTab === "users" && (
          <div className="cust-tab-box">
            <h3>All Users</h3>
            <table className="cust-table">
              <thead>
                <tr><th>ID</th><th>Name</th><th>NIC</th><th>Email</th><th>Phone</th></tr>
              </thead>
              <tbody>
                {users.map(u => (
                  <tr key={u.id}>
                    <td>{u.id}</td>
                    <td>{u.firstName} {u.lastName}</td>
                    <td>{u.nic}</td>
                    <td>{u.email}</td>
                    <td>{u.phone}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

      </div>
    </div>
  );
};

export default AdminDashboard;
