import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("profile");
  const [adminData, setAdminData] = useState({ fullName:"", email:"", phone:"", password:"", confirmPassword:"" });
  const [users, setUsers] = useState([]);
  const [complaints, setComplaints] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [userPopup, setUserPopup] = useState({visible:false, user:{}, role:"Citizen"});
  const [logoutPopup, setLogoutPopup] = useState(false);

  useEffect(() => {
    const email = localStorage.getItem("userEmail");
    if(!email) return setError("No email found in localStorage.");

    axios.get(`http://localhost:8081/Complaint-chain/api/auth/admin?email=${email}`)
      .then(res => setAdminData({...res.data, password:"", confirmPassword:""}))
      .catch(() => setError("❌ Failed to fetch admin profile."));

    loadUsers();
    loadComplaints();
  }, []);

  const loadUsers = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/users")
      .then(res => setUsers(res.data))
      .catch(() => setError("❌ Failed to load users."));
  };

  const loadComplaints = () => {
    axios.get("http://localhost:8081/Complaint-chain/api/complaints/all")
      .then(res => setComplaints(res.data))
      .catch(() => setError("❌ Failed to load complaints."));
  };

  const handleAdminChange = e => setAdminData({...adminData, [e.target.name]: e.target.value});

  const handleProfileUpdate = e => {
    e.preventDefault();
    if(adminData.password && adminData.password!==adminData.confirmPassword) {
      setError("❌ Passwords do not match!"); setTimeout(()=>setError(""),2500); return;
    }
    axios.put("http://localhost:8081/Complaint-chain/api/auth/admin/update", adminData)
      .then(()=> { setMessage("✅ Profile updated successfully!"); setTimeout(()=>setMessage(""),2500); })
      .catch(()=>{ setError("❌ Failed to update profile."); setTimeout(()=>setError(""),2500); });
  };

  const handleUserRoleUpdate = () => {
    axios.put(`http://localhost:8081/Complaint-chain/api/users/${userPopup.user.id}/role`, {role:userPopup.role})
      .then(()=> { setMessage("✅ User role updated!"); setTimeout(()=>setMessage(""),2500); loadUsers(); setUserPopup({visible:false, user:{}, role:"Citizen"}); })
      .catch(()=>{ setError("❌ Failed to update role."); setTimeout(()=>setError(""),2500); });
  };

  const handleLogout = () => { localStorage.removeItem("userEmail"); window.location.href="/"; };

  return (
    <div className="cust-home-page">
      <div className="cust-home-container">
        <h1>Admin Dashboard</h1>
        {error && <div className="cust-error">{error}</div>}
        {message && <div className="popupOverlay"><div className="successMessagePopup">{message}</div></div>}

        <div className="cust-btn-group">
          <button className={`cust-btn ${activeTab==="profile"?"cust-btn-active":""}`} onClick={()=>setActiveTab("profile")}>Profile</button>
          <button className={`cust-btn ${activeTab==="users"?"cust-btn-active":""}`} onClick={()=>setActiveTab("users")}>Manage Users</button>
          <button className={`cust-btn ${activeTab==="complaints"?"cust-btn-active":""}`} onClick={()=>setActiveTab("complaints")}>Complaints</button>
        </div>

        {/* Profile Tab */}
        {activeTab==="profile" && (
          <div className="cust-tab-box">
            <h3>Admin Profile</h3>
            <form onSubmit={handleProfileUpdate} className="cust-form-grid">
              <div className="form-group full-width"><label>Full Name:</label><input type="text" name="fullName" value={adminData.fullName} onChange={handleAdminChange}/></div>
              <div className="form-group"><label>Email:</label><input type="email" name="email" value={adminData.email} onChange={handleAdminChange}/></div>
              <div className="form-group"><label>Phone:</label><input type="text" name="phone" value={adminData.phone} onChange={handleAdminChange}/></div>
              <div className="form-group"><label>Password:</label><input type="password" name="password" value={adminData.password} onChange={handleAdminChange}/></div>
              <div className="form-group"><label>Confirm Password:</label><input type="password" name="confirmPassword" value={adminData.confirmPassword} onChange={handleAdminChange}/></div>
              <div className="form-group full-width" style={{textAlign:"center"}}><button type="submit" className="cust-btn-submit">Update Profile</button></div>
              <div style={{marginTop:"20px", textAlign:"center"}}><button onClick={()=>setLogoutPopup(true)} className="cust-btn-submit">Logout</button></div>
            </form>
          </div>
        )}

        {/* Users Tab */}
        {activeTab==="users" && (
          <div className="cust-tab-box">
            <h3>Manage Users</h3>
            <table className="cust-table">
              <thead><tr><th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Status</th></tr></thead>
              <tbody>
                {users.map(u=>(
                  <tr key={u.id} onClick={()=>setUserPopup({visible:true,user:u,role:u.role})} style={{cursor:"pointer"}}>
                    <td>{u.id}</td><td>{u.fullName}</td><td>{u.email}</td><td>{u.role}</td><td>{u.active?"Active":"Inactive"}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* Complaints Tab */}
        {activeTab==="complaints" && (
          <div className="cust-tab-box">
            <h3>All Complaints</h3>
            <table className="cust-table">
              <thead><tr><th>ID</th><th>User</th><th>Department</th><th>Description</th><th>Priority</th><th>Status</th></tr></thead>
              <tbody>
                {complaints.map(c=>(
                  <tr key={c.id}>
                    <td>{c.id}</td><td>{c.userName}</td><td>{c.department}</td><td>{c.description}</td><td>{c.urgency}</td><td>{c.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* User Role Popup */}
        {userPopup.visible && (
          <div className="popupOverlay">
            <div className="cancelPopup">
              <h3>Update User Role</h3>
              <p><strong>{userPopup.user.fullName}</strong></p>
              <select value={userPopup.role} onChange={e=>setUserPopup({...userPopup,role:e.target.value})}>
                <option>Citizen</option>
                <option>Staff</option>
                <option>Manager</option>
                <option>Admin</option>
              </select>
              <div className="popupButtons">
                <button onClick={handleUserRoleUpdate}>Update</button>
                <button onClick={()=>setUserPopup({visible:false,user:{},role:"Citizen"})}>Cancel</button>
              </div>
            </div>
          </div>
        )}

        {/* Logout Popup */}
        {logoutPopup && (
          <div className="popupOverlay">
            <div className="cancelPopup">
              <h3>Confirm Logout</h3>
              <p>Are you sure you want to logout?</p>
              <div className="popupButtons">
                <button onClick={handleLogout}>Yes, Logout</button>
                <button onClick={()=>setLogoutPopup(false)}>Cancel</button>
              </div>
            </div>
          </div>
        )}

      </div>
    </div>
  );
};

export default AdminDashboard;
