import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AuthorityDashboard.css";

const AuthorityDashboard = ({ authorityId }) => {
  const [activeTab, setActiveTab] = useState("pending");
  const [userData, setUserData] = useState({ name: "", email: "" });
  const [complaints, setComplaints] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [fieldErrors, setFieldErrors] = useState({});
  const [updatePopup, setUpdatePopup] = useState({ visible: false, complaint: null });
  const [statusFilter, setStatusFilter] = useState("pending");
  const [priorityFilter, setPriorityFilter] = useState("high-low");

  // Load authority info
  useEffect(() => {
    axios.get(`http://localhost:8081/Complaint-chain/api/auth/authority/${authorityId}`)
      .then(res => setUserData(res.data))
      .catch(() => setError("❌ Failed to fetch authority details."));
  }, [authorityId]);

  // Load complaints based on status and priority
  useEffect(() => {
    loadComplaints(statusFilter, priorityFilter);
  }, [statusFilter, priorityFilter]);

  const loadComplaints = (status = statusFilter, sort = priorityFilter) => {
    axios.get(`http://localhost:8081/Complaint-chain/api/complaints/${status}?sort=${sort}`)
      .then(res => setComplaints(res.data))
      .catch(() => setError("❌ Failed to load complaints."));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatePopup({ ...updatePopup, complaint: { ...updatePopup.complaint, [name]: value } });
    setFieldErrors({ ...fieldErrors, [name]: "" });
  };

  const handleStatusUpdate = () => {
    const { complaint } = updatePopup;
    if (!complaint.status) { setError("❌ Status is required."); return; }

    axios.put(`http://localhost:8081/Complaint-chain/api/complaints/update/${complaint.id}`, complaint)
      .then(() => {
        setMessage("✅ Complaint updated successfully!");
        loadComplaints();
        setUpdatePopup({ visible: false, complaint: null });
        setTimeout(() => setMessage(""), 2500);
      })
      .catch(() => { setError("❌ Failed to update complaint."); setTimeout(() => setError(""), 2500); });
  };

  return (
    <div className="auth-home-page">
      <div className="auth-home-container">
        <h1>Authority Dashboard</h1>
        {error && <div className="auth-error">{error}</div>}
        {message && <div className="popupOverlay"><div className="successMessagePopup">{message}</div></div>}

        {/* Tabs */}
        <div className="auth-btn-group">
          <button className={`auth-btn ${activeTab==="pending"?"auth-btn-active":""}`} onClick={()=>setActiveTab("pending")}>Pending Complaints</button>
          <button className={`auth-btn ${activeTab==="inprogress"?"auth-btn-active":""}`} onClick={()=>setActiveTab("inprogress")}>In Progress</button>
          <button className={`auth-btn ${activeTab==="completed"?"auth-btn-active":""}`} onClick={()=>setActiveTab("completed")}>Completed Complaints</button>
        </div>

        {/* Filters */}
        <div className="filterContainer">
          <label>Status: </label>
          <select value={statusFilter} onChange={(e)=>setStatusFilter(e.target.value)}>
            <option value="pending">Pending</option>
            <option value="inprogress">In Progress</option>
            <option value="completed">Completed</option>
          </select>
          <label>Sort by: </label>
          <select value={priorityFilter} onChange={(e)=>setPriorityFilter(e.target.value)}>
            <option value="high-low">Priority: High → Low</option>
            <option value="low-high">Priority: Low → High</option>
            <option value="date-asc">Date: Ascending</option>
            <option value="date-desc">Date: Descending</option>
          </select>
        </div>

        {/* Complaints Table */}
        <div className="auth-tab-box">
          <table className="auth-table">
            <thead>
              <tr><th>ID</th><th>Citizen</th><th>Department</th><th>Priority</th><th>Description</th><th>Status</th></tr>
            </thead>
            <tbody>
              {complaints.map(c=>(
                <tr key={c.id} onClick={()=>setUpdatePopup({ visible:true, complaint: c })}>
                  <td>{c.id}</td>
                  <td>{c.name}</td>
                  <td>{c.department}</td>
                  <td>{c.urgency}</td>
                  <td>{c.description}</td>
                  <td>{c.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Update Popup */}
        {updatePopup.visible && (
          <div className="popupOverlay">
            <div className="updatePopup">
              <h3>Update Complaint Status</h3>
              <p><strong>{updatePopup.complaint.description}</strong></p>
              <label>Status:</label>
              <select name="status" value={updatePopup.complaint.status} onChange={handleChange}>
                <option value="pending">Pending</option>
                <option value="inprogress">In Progress</option>
                <option value="completed">Completed</option>
              </select>
              <div className="popupButtons">
                <button onClick={handleStatusUpdate}>Update</button>
                <button onClick={()=>setUpdatePopup({ visible:false, complaint:null })}>Cancel</button>
              </div>
            </div>
          </div>
        )}

      </div>
    </div>
  );
};

export default AuthorityDashboard;
