import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import styles from "./Login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    setError("");
    if (!email || !password) {
      setError("Please enter valid credentials.");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8081/Complaint-chain/api/auth/login",
        { email, password }
      );

      const firstName = response.data.firstName;
      setSuccessMessage("Welcome " + firstName);

      // Clear input
      setEmail("");
      setPassword("");

      // Redirect after short delay (like registration)
      setTimeout(() => {
        setSuccessMessage("");
        navigate("/dashboard");
      }, 2000); // 2 seconds delay
    } catch (err) {
      if (err.response && err.response.status === 401) {
        setError("Invalid email or password.");
      } else {
        setError("Network error. Please try again.");
      }
    }
  };

  return (
    <div className={styles.loginPageContainer}>
      {/* Left Side */}
      <div className={styles.loginPageLeft}>
        <div>
          <h1>Welcome Back, Citizen</h1>
          <p>
            Log in to access your portal, manage complaints, and stay updated
            on services that matter to you.
          </p>
        </div>
      </div>

      {/* Right Side */}
      <div className={styles.loginPageRight}>
        <div className={styles.loginPageBox}>
          <h2>Login</h2>

          {error && <div className={styles.errorMessage}>{error}</div>}
          {successMessage && (
            <div className={styles.successMessage}>{successMessage}</div>
          )}

          <form onSubmit={handleSubmit}>
            <div className={styles.loginPageFormGroup}>
              <label htmlFor="email">Email</label>
              <input
                type="text"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className={styles.loginPageFormGroup}>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            <button type="submit" className={styles.loginPageButton}>
              Login
            </button>

            <div className={styles.loginPageExtraLinks}>
              <p>
                <a href="#">Forgot Password?</a>
              </p>
              <p>
                Don’t have an account? <a href="/register">Register</a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
