import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState("track");
  const [complaints, setComplaints] = useState([]);
  const [cancelledComplaints, setCancelledComplaints] = useState([]);
  const [authorities, setAuthorities] = useState([]);
  const [trackFilter, setTrackFilter] = useState("high-low");
  const [trackStatus, setTrackStatus] = useState("pending");
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // ------------------- Fetch functions -------------------
  const loadComplaints = () => {
    axios
      .get(`http://localhost:8081/Complaint-chain/api/complaints/${trackStatus}?sort=${trackFilter}`)
      .then((res) => setComplaints(res.data))
      .catch(() => setError("❌ Failed to load complaints."));
  };

  const loadCancelledComplaints = () => {
    axios
      .get("http://localhost:8081/Complaint-chain/api/complaints/cancelled")
      .then((res) => setCancelledComplaints(res.data))
      .catch(() => setError("❌ Failed to load cancelled complaints."));
  };

  const loadAuthorities = () => {
    axios
      .get("http://localhost:8081/Complaint-chain/api/authority")
      .then((res) => setAuthorities(res.data))
      .catch(() => setError("❌ Failed to load authorities."));
  };

  useEffect(() => {
    if (activeTab === "track") loadComplaints();
    if (activeTab === "cancelled") loadCancelledComplaints();
    if (activeTab === "assign") {
      loadComplaints();
      loadAuthorities();
    }
  }, [activeTab, trackFilter, trackStatus]);

  // ------------------- Update complaint status -------------------
  const updateStatus = (id, status) => {
    axios
      .put(`http://localhost:8081/Complaint-chain/api/complaints/${id}/status?status=${status}`)
      .then(() => {
        setMessage(`✅ Complaint ${id} status updated to ${status}!`);
        setTimeout(() => setMessage(""), 2500);
        loadComplaints();
      })
      .catch(() => setError("❌ Failed to update status."));
  };

  // ------------------- Assign Task -------------------
  const assignTask = (complaint) => {
    if (!complaint.selectedAuthority || !complaint.deadline) {
      setError("❌ Please select authority and deadline.");
      setTimeout(() => setError(""), 3000);
      return;
    }
    axios
      .post("http://localhost:8081/Complaint-chain/api/tasks", {
        complaintId: complaint.id,
        authorityId: complaint.selectedAuthority,
        deadline: complaint.deadline,
      })
      .then(() => {
        setMessage(`✅ Complaint ${complaint.id} assigned!`);
        setTimeout(() => setMessage(""), 2500);
        loadComplaints();
      })
      .catch(() => setError("❌ Failed to assign complaint."));
  };

  return (
    <div className="cust-home-page">
      <div className="cust-home-container">
        <h1>Admin Dashboard</h1>
        {error && <div className="cust-error">{error}</div>}
        {message && (
          <div className="popupOverlay themedPopup">
            <div className="successMessagePopup">{message}</div>
          </div>
        )}

        {/* Tabs */}
        <div className="cust-btn-group">
          {["track", "cancelled", "assign"].map((tab) => (
            <button
              key={tab}
              className={`cust-btn ${activeTab === tab ? "cust-btn-active" : ""}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1).replace(/([A-Z])/g, " $1")}
            </button>
          ))}
        </div>

        {/* ------------------- Track Complaints ------------------- */}
        {activeTab === "track" && (
          <div className="cust-tab-box">
            <h3>Track Complaints</h3>
            <div className="filterContainer">
              <label>Status: </label>
              <select value={trackStatus} onChange={(e) => setTrackStatus(e.target.value)}>
                <option value="pending">Pending</option>
                <option value="inprogress">In Progress</option>
              </select>
              <label>Sort by: </label>
              <select value={trackFilter} onChange={(e) => setTrackFilter(e.target.value)}>
                <option value="high-low">Priority: High → Low</option>
                <option value="low-high">Priority: Low → High</option>
                <option value="date-asc">Date: Ascending</option>
                <option value="date-desc">Date: Descending</option>
              </select>
            </div>
            <table className="cust-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Dept</th>
                  <th>Priority</th>
                  <th>Description</th>
                  <th>Date</th>
                  <th>Status</th>
                  <th>Update Status</th>
                </tr>
              </thead>
              <tbody>
                {complaints.map((c) => (
                  <tr key={c.id}>
                    <td>{c.id}</td>
                    <td>{c.department}</td>
                    <td>{c.urgency}</td>
                    <td>{c.description}</td>
                    <td>{new Date(c.submittedAt).toLocaleString()}</td>
                    <td>{c.status}</td>
                    <td>
                      {c.status.toLowerCase() !== "resolved" && (
                        <select
                          onChange={(e) => updateStatus(c.id, e.target.value)}
                          defaultValue=""
                        >
                          <option value="" disabled>
                            Change Status
                          </option>
                          <option value="InProgress">In Progress</option>
                          <option value="Resolved">Resolved</option>
                        </select>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* ------------------- Cancelled Complaints ------------------- */}
        {activeTab === "cancelled" && (
          <div className="cust-tab-box">
            <h3>Cancelled Complaints</h3>
            <table className="cust-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Dept</th>
                  <th>Priority</th>
                  <th>Description</th>
                  <th>Reason</th>
                  <th>Cancelled At</th>
                </tr>
              </thead>
              <tbody>
                {cancelledComplaints.map((c) => (
                  <tr key={c.id}>
                    <td>{c.originalId}</td>
                    <td>{c.department}</td>
                    <td>{c.urgency}</td>
                    <td>{c.description}</td>
                    <td>{c.reason}</td>
                    <td>{new Date(c.submittedAt).toLocaleString()}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* ------------------- Assign Tasks ------------------- */}
        {activeTab === "assign" && (
          <div className="cust-tab-box">
            <h3>Assign Complaints to Authorities</h3>
            <table className="cust-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Dept</th>
                  <th>Priority</th>
                  <th>Description</th>
                  <th>Date</th>
                  <th>Authority</th>
                  <th>Deadline</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {complaints
                  .filter((c) => c.status.toLowerCase() === "pending")
                  .map((c) => {
                    const relevantAuthorities = authorities.filter(
                      (a) => a.department === c.department
                    );
                    return (
                      <tr key={c.id}>
                        <td>{c.id}</td>
                        <td>{c.department}</td>
                        <td>{c.urgency}</td>
                        <td>{c.description}</td>
                        <td>{new Date(c.submittedAt).toLocaleString()}</td>
                        <td>
                          <select
                            value={c.selectedAuthority || ""}
                            onChange={(e) =>
                              setComplaints((prev) =>
                                prev.map((item) =>
                                  item.id === c.id
                                    ? { ...item, selectedAuthority: e.target.value }
                                    : item
                                )
                              )
                            }
                          >
                            <option value="">Select Authority</option>
                            {relevantAuthorities.map((a) => (
                              <option key={a.id} value={a.id}>
                                {a.name} ({a.level})
                              </option>
                            ))}
                          </select>
                        </td>
                        <td>
                          <input
                            type="date"
                            value={c.deadline || ""}
                            onChange={(e) =>
                              setComplaints((prev) =>
                                prev.map((item) =>
                                  item.id === c.id ? { ...item, deadline: e.target.value } : item
                                )
                              )
                            }
                          />
                        </td>
                        <td>
                          <button
                            className="cust-btn-submit"
                            onClick={() => assignTask(c)}
                          >
                            Assign
                          </button>
                        </td>
                      </tr>
                    );
                  })}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
