import React, { useState } from "react";
import axios from "axios";
import styles from "./Register.module.css";

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    dob: "",
    nic: "",
    password: "",
    confirmPassword: ""
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
    setErrors({ ...errors, [e.target.id]: "" });
  };

  const validateForm = () => {
    let newErrors = {};

    if (!formData.firstName.trim()) newErrors.firstName = "First name is required";
    if (!formData.lastName.trim()) newErrors.lastName = "Last name is required";

    if (!formData.email) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Invalid email format";
    }

    if (!formData.phone) {
      newErrors.phone = "Phone number is required";
    } else if (!/^\d{10}$/.test(formData.phone)) {
      newErrors.phone = "Phone must be 10 digits";
    }

    if (!formData.dob) newErrors.dob = "Date of birth is required";

    if (!formData.nic) {
      newErrors.nic = "NIC is required";
    } else if (!/^\d{9}[Vv]$|^\d{12}$/.test(formData.nic)) {
      newErrors.nic = "NIC must be 9 digits + V or 12 digits";
    }

    if (!formData.password) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters";
    }

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
    }

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = validateForm();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      const payload = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        phone: formData.phone,
        dob: formData.dob,
        nic: formData.nic,
        password: formData.password
      };

      const response = await axios.post(
        "http://localhost:8081/Complaint-chain/api/auth/register",
        payload
      );

      setSuccessMessage("✅ Registration successful! Welcome " + response.data.firstName);
      setErrors({});

      setFormData({
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
        dob: "",
        nic: "",
        password: "",
        confirmPassword: ""
      });

      setTimeout(() => setSuccessMessage(""), 3000);

    } catch (error) {
      if (error.response && error.response.data) {
        const backendMsg = error.response.data.message || "";
        let newErrors = {};

        if (backendMsg.toLowerCase().includes("email")) {
          newErrors.email = "This email is already registered";
        }
        if (backendMsg.toLowerCase().includes("nic")) {
          newErrors.nic = "This NIC is already registered";
        }
        if (!newErrors.email && !newErrors.nic) {
          newErrors.general = backendMsg || "Registration failed!";
        }

        setErrors(newErrors);
      } else {
        setErrors({ general: "Network error. Please try again." });
      }
    }
  };

  return (
    <div className={styles.registerPageContainer}>
      <div className={styles.registerPageLeft}>
        <div>
          <h1>Join the Citizen Portal</h1>
          <p>Register to submit complaints, track progress, and stay connected with public services.</p>
        </div>
      </div>

      <div className={styles.registerPageRight}>
        <div className={styles.registerPageBox}>
          <h2>Register</h2>

          {errors.general && <div className={styles.errorMessage}>{errors.general}</div>}

          <form className={styles.registerPageForm} onSubmit={handleSubmit}>
            {["firstName","lastName","email","phone","dob","nic","password","confirmPassword"].map(field => (
              <div key={field} className={styles.registerPageFormGroup}>
                <label htmlFor={field}>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
                <input
                  type={field.includes("password") ? "password" : field === "dob" ? "date" : "text"}
                  id={field}
                  value={formData[field]}
                  onChange={handleChange}
                  required
                />
                {errors[field] && <p className={styles.errorText}>{errors[field]}</p>}
              </div>
            ))}
            <div className={`${styles.registerPageFormGroup} ${styles.registerPageFullWidth}`}>
              <button className={styles.registerPageButton} type="submit">Register</button>
            </div>
            <div className={styles.registerPageExtraLinks}>
              <p>Already have an account? <a href="/login">Login</a></p>
            </div>
          </form>
        </div>
      </div>

      {/* Centered success popup */}
      {successMessage && (
        <div className={styles.successPopup}>{successMessage}</div>
      )}
    </div>
  );
};

export default Register;
